INSERT INTO `position` VALUES (1,'Professor','Conducts research, teaches courses, and mentors students in specialized academic fields. Contributes to curriculum development and departmental leadership.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(2,'Associate Professor','Teaches undergraduate and graduate courses, conducts research, and provides academic guidance to students. Participates in departmental committees.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(3,'Teaching Assistant','Supports professors in class preparation, grading assignments, and providing additional help to students. Often assists with research projects.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(4,'Administrative Coordinator','Manages office operations, maintains records, coordinates meetings and events, and serves as a liaison between departments.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(5,'Student Counselor','Provides academic and personal guidance to students. Assists with course selection, career planning, and addresses various student concerns.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(6,'Librarian','Manages library collections, assists faculty and students with research, and maintains library systems and databases. Provides information literacy training.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(7,'IT Specialist','Provides technical support, maintains computer systems and networks, and implements new technologies across campus departments.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(8,'HR Specialist','Handles employee relations, recruitment, benefits administration, and ensures compliance with employment laws and university policies.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(9,'Financial Analyst','Manages budgets, prepares financial reports, processes transactions, and provides guidance on financial decisions across departments.','2025-06-20 14:59:19','2025-07-20 16:00:31'),(10,'Facilities Technician','Maintains and repairs campus buildings, grounds, and equipment. Ensures safe and functional environments for students, faculty, and staff.','2025-06-20 14:59:19','2025-07-20 16:00:31');
